package types

import (
	"fmt"
)

func Example_float() {
	f := .0
	f1 := 6.67428e-11
	f2 := 106.23451
	f3 := 1_1.
	f4 := 024.1

	fmt.Printf("f %T %f\n", f, f)
	fmt.Printf("f1 %T %v\n", f1, f1)
	fmt.Printf("f2 %T %v\n", f2, f2)
	fmt.Printf("f3 %T %v\n", f3, f3)
	fmt.Printf("f4 %T %v\n", f4, f4)

	// Output:
	// f float64 0.000000
	// f1 float64 6.67428e-11
	// f2 float64 106.23451
	// f3 float64 11
	// f4 float64 24.1
}

func Example_float32() {
	var f32 float32 = 3.4e+38
	var f32min float32 = -3.4e+38

	fmt.Printf("f32 max %T %f\n", f32, f32)
	fmt.Printf("f32 min %T %f\n", f32min, f32min)

	// Output:
	// f32 max float32 339999995214436424907732413799364296704.000000
	// f32 min float32 -339999995214436424907732413799364296704.000000
}

func Example_float64() {
	var f64 float64 = +1.7e+308
	var f64min float64 = -1.7e+308

	fmt.Printf("f64 max %T %f\n", f64, f64)
	fmt.Printf("f64 min %T %f\n", f64min, f64min)

	// Output:
	// f64 max float64 .000000
	// f64 min float64 -.000000
}

func Example_printFloat() {

	f := 122.4375

	fmt.Printf("%f\n", f)
	fmt.Printf("%0.3f\n", f)
	fmt.Printf("%10f\n", f)
	fmt.Printf("%10.2f\n", f) // This will fill up all 10 spaces and pack left with unicode space

	// Output:
	// 122.437500
	// 122.438
	// 122.437500
	//     122.44

}
